document.addEventListener("DOMContentLoaded",function(){$("#signup-form").submit(function(n){n.preventDefault(),$.post("/signup",$("#signup-form").serialize(),function(n){n.success&&(window.location=n.redirect)})}),$("#login-form").submit(function(n){n.preventDefault(),$.post("/login",$("#login-form").serialize(),function(n){n.success&&(window.location=n.redirect)})})});